import React, {useState} from "react";
import './App.css';
import "bootstrap/dist/css/bootstrap.min.css";
import PersonList from "./PersonList"
import NewPerson from "./NewPerson"
//import uuid from "uuid/v1";

function Person() {
  const initialData = [
    { id: 1/*uuid()*/, personName: "Hanne" },
    { id: 2/*uuid()*/, personName: "Sanne" },
    { id: 3/*uuid()*/, personName: "Søren" }
  ]
  const [persons, setPersons] = useState(initialData);
  const [newPerson, setNewPerson] = useState({ id: "", personName: "" });
  console.log(persons)

  const addTodo = todo => {
    if (todo.id === "") { // id=-1 Indicates a new object
      todo.id = 4/*uuid()*/;
      persons.push(todo);
    } else {//if id != "", it's an existing todo. Find it and add changes
      let todoToEdit = persons.find(t => t.id === todo.id);
      todoToEdit.personName = todo.personName;
    }
    setPersons([...persons]);
    setNewPerson({id:"", personName:""})
  };

  return (
    <div className="container outer">
      <h2 style={{ textAlign: "center", marginBottom:25 }}>
        Props and Lifting State Demo
      </h2>

      <div className="row">
        <div className="col-6 allTodos">
          <PersonList persons={persons} />
        </div>
        <div className="col-5 new-todo">
          <NewPerson
            addTodo={addTodo}
            nextTodo={newPerson}            
          />
        </div>
      </div>
    </div>
  );
}
export default Person;
